import React from 'react';
import './Contact.css';   
import emailjs from "emailjs-com";
import contactImg from "./img/about-9.png";

export default function ContactUs() {


    function sendEmail(e) {
        e.preventDefault();

    emailjs.sendForm('gmail_paulo', 'template_gwwdfdg', e.target, 'user_WRbuOJOeUKm3u6hk8L9FS')

        .then((result) => {
            alert("Mensagem enviada com sucesso! 👍");
           
        }, (error) => {
            alert(error.message)
            
        });
        e.target.reset()

 
    }
    return(
<div className="contact component__space" id="Contact">
            <div className="row">
                <div className="col__2">
                    <div className="contact__box">
                        <div className="contact__meta">
                            <h1 className="hire__text">Contrate - Me.</h1>
                            <p className="hire__text white">Estou disponível para trabalho autônomo. Conecte-se comigo por telefone:</p>
                           <p className="hire__text white"><strong>+55 53 99947 - 1481</strong> e email <strong>paulomadson16@gmail.com</strong></p>
                        </div>
            <div className="container">
            <h2>Contato</h2>
            <form onSubmit={sendEmail}>                
                        <div className="input__box">
                        <label>Nome</label>  <input type="text" autoFocus className="contact name" required placeholder="Nome" name="name"/>
                        <label>Email</label>  <input type="email" className="contact email" required placeholder="Seu email" name="email"/>
                        <label>Mensagem</label>    <textarea name="message" id="message" placeholder="Escreva sua mensagem"></textarea>
                        <input type="submit" className="btn contact pointer" value="Enviar mensagem"></input>
                        </div>
                        <div className="col__2">
                    <img src={contactImg} alt="" className="contact__img" />
                </div>
                </form>
            </div>
        </div>
    </div>
</div>
</div>

    
    )
}
