import React from 'react';
import './Contact.css';
import emailjs from 'emailjs-com';
import contactImg from "./img/about-9.png";

export default function Contact() {

     function enviarEmail(e){
      e.preventDefault();

    emailjs.sendForm('YOUR_SERVICE_ID', 'YOUR_TEMPLATE_ID', form.current, 'YOUR_USER_ID')
      .then((result) => {
          alert("Mensagem enviada com sucesso! üëç");
      }, (error) => {
          alert(error.message)
      });

      }

    return (
        <div className="contact component__space" id="Contact">
            <div className="row">
                <div className="col__2">
                    <div className="contact__box">
                        <div className="contact__meta">
                            <h1 className="hire__text">Contrate - Me.</h1>
                            <p className="hire__text white">Estou dispon√≠vel para trabalho aut√¥nomo. Conecte-se comigo por telefone:</p>
                           <p className="hire__text white"><strong>+55 53 99947 - 1481</strong> e email <strong>paulomadson16@gmail.com</strong></p>
                        </div>
                        <div className="enviarEmail">

                           <input type="text" className="contact name" placeholder="Seu Nome *" required />
                           <input type="text" className="contact email" placeholder="Seu Email *" required />
                           <input type="text" className="contact subject" placeholder="Escreva um assunto" required />
                           <textarea name="message" id="message" placeholder="Escreva sua mensagem" required ></textarea>
                           <button className="btn contact pointer" type="submit">Enviar</button>
                        </div>
                    </div>
                </div>
                <div className="col__2">
                    <img src={contactImg} alt="" className="contact__img" />
                </div>
            </div>
        </div>
    )
}
